require_once(ABSPATH . 'wp-admin/includes/media.php');
require_once(ABSPATH . 'wp-admin/includes/file.php');
require_once(ABSPATH . 'wp-admin/includes/image.php');

	function upload_news(){



		global $news_items;
		global $wpdb;


			$wp_upload_dir = wp_upload_dir();
			$field_title = 'Post Upload';
			$field_post_name = $wpdb->get_var( "SELECT post_name FROM $wpdb->posts WHERE post_title = '" . $field_title . "'" );
			$find_date = array('janvier', 'février', 'mars', 'avril', 'mai', 'juin', 'juillet', 'août', 'septembre', 'novembre', 'décembre');
			$replace_date = array('January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December');


		foreach ($news_items as $news_item) {




			$title = preg_replace("/&nbsp;/", " ", $news_item->title);


			$date = explode('-', $title)[0];
			$date = str_replace($find_date, $replace_date, strtolower($date));
			echo $date;
			$post_date = date('Y/m/d', strtotime($date));

			$post_args = array(
			  'post_title'    => $title,
			  'post_content'  => $news_item->content,
			  'post_status'   => 'publish',
			  'post_type'   => 'post',
			  'post_date'   => $post_date,
			  'post_author'   => 1
			);
			 
			// Insert the post into the database
			$post_id = wp_insert_post( $post_args );


			if (  isset($news_item->image) ):
			
				$url = $news_item->image;
				$desc = "";	
				$filename = media_sideload_image($url, $post_id, $desc, 'src');

				$attachments = get_posts( array(
				    'post_type' => 'attachment',
				    'posts_per_page' => 1,
				    'post_parent' => $post_id
				) );

				if(isset($attachments[0]->ID)) {
					$attach_id = $attachments[0]->ID;
					add_post_meta($post_id, '_thumbnail_id', $attach_id); 

				};

			endif; // IF IMAGE

		

			if (  isset($news_item->pdf) ):

				$pdf = $news_item->pdf;
				$desc = "";	
				$pdf_name = media_sideload_image($pdf, $post_id, $desc);
				$file_name = $wp_upload_dir['basedir'] . '/2016/05/' . $post_id . '_news.pdf';

				$pdf_contents =  file_get_contents($pdf);
				file_put_contents($file_name, $pdf_contents);


				$pdf_filetype = wp_check_filetype( basename( $file_name ), null );

				// Prepare an array of post data for the attachment.
				$attachment_pdf = array(
					'guid'           => $wp_upload_dir['url'] . '/' . basename( $file_name ), 
					'post_mime_type' => $pdf_filetype['type'],
					'post_title'     => preg_replace( '/\.[^.]+$/', '', basename( $file_name ) ),
					'post_content'   => '',
					'post_status'    => 'inherit'
				);

				// Insert the attachment.
				$attach_id = wp_insert_attachment( $attachment_pdf, $file_name, $post_id );

		


					add_post_meta($post_id, '_post_upload', $field_post_name); 
					add_post_meta($post_id, 'post_upload', $attach_id); 




			endif;



// Full texts	
// meta_id
// post_id
// meta_key
// meta_value

// Edit Edit
